import React from 'react';
import { useNavigate } from 'react-router-dom';

const RoleSelection = ({ auth }) => {
  const navigate = useNavigate();
  if (auth.role === 'jobseeker') navigate('/jobseeker');
  else if (auth.role === 'recruiter') navigate('/recruiter');
  return null;
};

export default RoleSelection;
