import React from 'react';

const ResultCard = ({ result }) => {
  const { ats_score, cover_letters, job_roles, job_suggestions } = result;

  return (
    <div className="bg-blue-800 mt-8 p-6 rounded-2xl shadow-lg text-white">
      <h2 className="text-2xl font-semibold mb-4 text-center">ðŸ“Š Resume Analysis Result</h2>

      <div className="mb-4">
        <strong>ATS Score:</strong> <span className="text-yellow-300">{ats_score} / 100</span>
      </div>

      <div className="mb-4">
        <strong>Detected Job Roles:</strong>
        <ul className="list-disc list-inside mt-1">
          {job_roles && job_roles.map((role, idx) => (
            <li key={idx}>{role}</li>
          ))}
        </ul>
      </div>

      <div className="mb-4">
        <strong>Job Suggestions:</strong>
        <ul className="list-disc list-inside mt-1">
          {job_suggestions && job_suggestions.map((job, idx) => (
            <li key={idx}>{job}</li>
          ))}
        </ul>
      </div>

      <div>
        <strong>Cover Letter Options:</strong>
        <ul className="list-disc list-inside mt-1">
          {cover_letters && cover_letters.map((cl, idx) => (
            <li key={idx} className="mt-2 bg-blue-900 p-3 rounded">
              {cl}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ResultCard;
