import React from 'react';

const ResultCard = ({ result }) => {
  if (!result) return null;

  const jobRoles = Array.isArray(result.Job_Roles) ? result.Job_Roles : [];

  // Copy to clipboard function
  const handleCopy = () => {
    navigator.clipboard.writeText(result.Cover_Letter);
    alert("Cover letter copied to clipboard!");
  };

  // Download as .txt function
  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([result.Cover_Letter], { type: 'text/plain' });
    element.href = URL.createObjectURL(file);
    element.download = "cover_letter.txt";
    document.body.appendChild(element);
    element.click();
    document.body.removeChild(element);
  };

  return (
    <div className="mt-6 bg-white p-5 rounded-xl shadow-md">
      <h2 className="text-xl font-semibold mb-2 text-blue-900">Results:</h2>

      <p><strong>üìä ATS Score:</strong> {result.ATS_Score}%</p>

      <p className="mt-4"><strong>üìÑ Suggested Cover Letter:</strong></p>
      <div className="bg-gray-100 p-3 rounded relative">
        <pre className="whitespace-pre-wrap text-sm">{result.Cover_Letter}</pre>
        <div className="flex gap-4 mt-2">
          <button
            onClick={handleCopy}
            className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600 text-sm"
          >
            Copy to Clipboard
          </button>
          <button
            onClick={handleDownload}
            className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600 text-sm"
          >
            Download as .txt
          </button>
        </div>
      </div>

      <h3 className="mt-5 font-semibold text-blue-900">üîç SiuJob Roles & Opportunities:</h3>
      <ul className="list-disc ml-5 text-sm">
        {jobRoles.length > 0 ? (
          jobRoles.map((job, i) => {
            if (typeof job === 'string') {
              return <li key={i}>{job}</li>;
            } else if (job?.title && job?.company && job?.link) {
              return (
                <li key={i}>
                  <a href={job.link} className="text-blue-600 underline" target="_blank" rel="noreferrer">
                    {job.title} at {job.company} ({job.location || 'Remote'})
                  </a>
                </li>
              );
            } else {
              return <li key={i}>Invalid job entry</li>;
            }
          })
        ) : (
          <li>No job roles found.</li>
        )}
      </ul>
    </div>
  );
};

export default ResultCard;
