import React, { useState } from 'react';
import axios from 'axios';
import ResultCard from './ResultCard';

const UploadForm = () => {
  const [resume, setResume] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!resume) {
      alert("Please select a resume file!");
      return;
    }

    if (
      resume &&
      ![
        'application/pdf',
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      ].includes(resume.type)
    ) {
      alert("Only PDF or DOCX files are allowed.");
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('resume', resume);

    try {
      const res = await axios.post('http://localhost:5000/upload-resume', formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setResult(res.data);
    } catch (err) {
      const errorMsg = err.response?.data?.details || "There was an error uploading your resume. Please try again.";
      setError(errorMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-blue-900 rounded-2xl shadow-md mt-10 text-white">
      <h1 className="text-2xl font-bold mb-4 text-center">üìÅ Upload Resume</h1>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          accept=".pdf,.docx"
          onChange={(e) => setResume(e.target.files[0])}
          className="mb-4 w-full p-2 border rounded text-black"
        />
        <button
          type="submit"
          className="bg-blue-700 hover:bg-blue-600 text-white px-4 py-2 rounded w-full"
          disabled={loading}
        >
          {loading ? "Uploading..." : "Upload & Analyze"}
        </button>
      </form>

      {error && <p className="text-red-400 mt-4">{error}</p>}
      {result && <ResultCard result={result} />}
    </div>
  );
};

export default UploadForm;
