import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './components/Auth/Login';
import Signup from './components/Auth/Signup';
import RoleSelection from './components/RoleSelection';
import UploadForm from './components/JobSeeker/UploadForm';
import RecruiterForm from './components/Recruiter/RecruiterForm';

const App = () => {
  const [auth, setAuth] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const decoded = JSON.parse(atob(token.split('.')[1]));
      setAuth({ username: decoded.username, role: decoded.role });
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login setAuth={setAuth} />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/role-selection" element={<RoleSelection auth={auth} />} />
        <Route path="/jobseeker" element={<UploadForm />} />
        <Route path="/recruiter" element={<RecruiterForm />} />
      </Routes>
    </Router>
  );
};

export default App;
