import React from 'react';

const ResultCard = ({ result }) => {
  // Ensure Job_Roles is an array before calling map
  const jobRoles = Array.isArray(result.Job_Roles) ? result.Job_Roles : [];

  return (
    <div className="mt-6 bg-card p-5 rounded-xl shadow-md">
      <h2 className="text-xl font-semibold mb-2 text-primary">Results:</h2>
      <p><strong>üìä ATS Score:</strong> {result.ATS_Score}</p>
      <p className="mt-2"><strong>üìÑ Suggested Cover Letter:</strong></p>
      <pre className="whitespace-pre-wrap text-sm bg-gray-100 p-3 rounded">{result.Cover_Letter}</pre>

      <h3 className="mt-4 font-semibold text-primary">üîç Job Roles & Opportunities:</h3>
      <ul className="list-disc ml-5 text-sm">
        {jobRoles.length > 0 ? (
          jobRoles.map((job, i) => (
            typeof job === 'string' ? (
              <li key={i}>{job}</li>
            ) : (
              <li key={i}>
                <a href={job.link} className="text-blue-600 underline" target="_blank" rel="noreferrer">
                  {job.title} at {job.company} ({job.location})
                </a>
              </li>
            )
          ))
        ) : (
          <li>No job roles found.</li>
        )}
      </ul>
    </div>
  );
};

export default ResultCard;
