import React, { useState } from 'react';
import UploadForm from './UploadForm';
import RecruiterForm from './RecruiterForm';

const App = () => {
  const [role, setRole] = useState('');

  const handleRoleSelect = (selectedRole) => {
    setRole(selectedRole);
  };

  return (
    <div className="min-h-screen bg-blue-950 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-8">Welcome to ResTrack</h1>

      {!role && (
        <div className="flex flex-col items-center gap-6">
          <p className="text-lg font-medium">Select your role:</p>
          <div className="flex gap-4">
            <button
              className="bg-blue-700 px-6 py-3 rounded-lg hover:bg-blue-600 transition"
              onClick={() => handleRoleSelect('jobseeker')}
            >
              üéØ Job Seeker
            </button>
            <button
              className="bg-green-700 px-6 py-3 rounded-lg hover:bg-green-600 transition"
              onClick={() => handleRoleSelect('recruiter')}
            >
              üßë‚Äçüíº Recruiter
            </button>
          </div>
        </div>
      )}

      {role === 'jobseeker' && <UploadForm />}
      {role === 'recruiter' && <RecruiterForm />}
    </div>
  );
};

export default App;
