import React, { useState } from 'react';
import axios from 'axios';
import ResultCard from './ResultCard';

const UploadForm = () => {
  const [resume, setResume] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);  // Add loading state
  const [error, setError] = useState(null);  // Add error state

  const handleUpload = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!resume) {
      alert("Please select a file!");
      return;
    }

    setLoading(true);  // Set loading to true when upload starts
    setError(null);  // Clear any previous error

    const formData = new FormData();
    formData.append('resume', resume);

    try {
      const res = await axios.post('http://localhost:5000/upload-resume', formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      // Set the result from backend response
      setResult(res.data);
    } catch (err) {
      console.error(err);
      setError("There was an error uploading your resume. Please try again.");
    } finally {
      setLoading(false);  // Set loading to false when request is complete
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-2xl shadow-md mt-10">
      <h1 className="text-2xl font-bold text-primary mb-4 text-center">AI Resume Analyzer</h1>
      <form onSubmit={handleUpload}>
        <input
          type="file"
          accept=".pdf,.txt"
          onChange={(e) => setResume(e.target.files[0])}
          className="mb-4 w-full p-2 border border-gray-300 rounded"
        />
        <button
          type="submit"
          className="bg-primary text-white px-4 py-2 rounded w-full"
          disabled={loading}  // Disable the button when loading
        >
          {loading ? "Uploading..." : "Upload & Analyze"}
        </button>
      </form>

      {error && <p className="text-red-500 mt-4">{error}</p>}  {/* Show error message */}
      {result && <ResultCard result={result} />}
    </div>
  );
};

export default UploadForm;
